mapper.insert:
        INSERT INTO restcomm_conference_detail_records (sid, date_created, date_updated, account_sid, status,
        friendly_name, api_version, uri, master_ms_id, master_ms_sdp) VALUES (#{sid},
		#{date_created}, #{date_updated}, #{account_sid}, #{status}, #{friendly_name}, #{api_version}, #{uri}, #{master_ms_id}, #{master_ms_sdp});

mapper.select:
SELECT COUNT(*) FROM restcomm_conference_detail_records WHERE account_sid=#{accountSid}

	AND status like #{status}


	AND friendly_name like #{friendlyName}


    AND date_created >= #{dateCreated}


	AND date_updated <= DATE_ADD(#{dateUpdated},INTERVAL 1 DAY) order by date_created

;

