	AND status like #{status}


	AND friendly_name like #{friendlyName}


    AND date_created >= #{dateCreated}


	AND date_updated <= DATE_ADD(#{dateUpdated},INTERVAL 1 DAY) order by date_created


LIMIT #{limit} OFFSET #{offset};

mapper.update:
UPDATE 
	restcomm_conference_detail_records 
	SET 
		date_updated=#{date_updated}, status=#{status}, master_ms_id=#{master_ms_id}, master_ms_sdp=#{master_ms_sdp} 
	WHERE sid=#{sid};
